import styled from "styled-components";

export const Button = styled.button`
  background: ${(p) => p.theme.gradients.button};
  border: none;
  border-radius: 10px;
  padding: 0.7rem 1.4rem;
  color: ${(p) => p.theme.colors.ink};
  font-weight: 500;
  box-shadow: 0 4px 8px ${(p) => p.theme.colors.softShadow};
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: ${(p) => p.theme.gradients.hover};
    transform: translateY(-2px);
    box-shadow: 0 6px 12px ${(p) => p.theme.colors.softShadow};
  }

  &:active {
    transform: translateY(0);
    box-shadow: 0 2px 6px ${(p) => p.theme.colors.softShadow};
  }
`;
