import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function TakeQuiz() {
  const { id } = useParams();
  const [quiz, setQuiz] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [score, setScore] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/quizzes`).then((res) => {
      const q = res.data.find((x) => x.id === Number(id));
      if (q) setQuiz(q);
    });
  }, [id]);

  const handleSubmit = async () => {
    let localScore = 0;
    quiz.data.questions.forEach((q, i) => {
      if (answers[i] === q.correctIndex) localScore++;
    });
    setScore(localScore);
    await axios.post("http://localhost:5000/api/attempts", { quizId: quiz.id, score: localScore });
  };

  if (!quiz) return <p>Loading quiz...</p>;

  return (
    <div>
      <h1>{quiz.title}</h1>
      {quiz.data.questions.map((q, qi) => (
        <div key={qi}>
          <p>{q.question}</p>
          {q.options.map((opt, oi) => (
            <label key={oi}>
              <input
                type="radio"
                name={`q${qi}`}
                value={oi}
                checked={answers[qi] === oi}
                onChange={() => {
                  const copy = [...answers];
                  copy[qi] = oi;
                  setAnswers(copy);
                }}
              />
              {opt}
            </label>
          ))}
        </div>
      ))}

      <button onClick={handleSubmit}>Submit Quiz</button>

      {score !== null && (
        <h2>Your score: {score} / {quiz.data.questions.length}</h2>
      )}
    </div>
  );
}
