import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

export default function QuizList() {
  const [quizzes, setQuizzes] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/quizzes").then((res) => setQuizzes(res.data));
  }, []);

  return (
    <div style={{ padding: "2rem" }}>
      <h1>ðŸ“š My Quizzes</h1>

      {quizzes.length === 0 ? (
        <p>No quizzes yet. Create one!</p>
      ) : (
        quizzes.map((q) => (
          <div
            key={q.id}
            style={{
              border: "1px solid #ddd",
              borderRadius: "8px",
              padding: "1rem",
              marginBottom: "1rem",
              background: "#fff7fa",
            }}
          >
            <h3>{q.title}</h3>
            <p>Type: {q.type}</p>

            <div style={{ marginTop: "0.5rem" }}>
              <Link to={`/quiz/${q.id}`} style={{ marginRight: "1rem" }}>
                ðŸŽ¯ Take Quiz
              </Link>
              <Link to={`/quiz/${q.id}/attempts`}>ðŸ“Š View Scores</Link>
            </div>
          </div>
        ))
      )}
    </div>
  );
}
