export const theme = {
  colors: {
    background: "#faf8f6",       // soft rice paper white
    surface: "#fffafc",          // pale sakura panel
    accent: "#e9a2b1",           // cherry blossom blush
    accentLight: "#f5c7ce",      // lighter petal tone
    ink: "#2f2f2f",              // sumi ink black
    gray: "#5c5c5c",             // soft twilight gray
    jade: "#a8b8a2",             // subtle green for balance
  },
  gradients: {
    sakura: "linear-gradient(90deg, #f7b8c6 0%, #fbd3df 100%)",
    sumi: "linear-gradient(90deg, #2f2f2f 0%, #5c5c5c 100%)",
  },
  fonts: {
    main: "'Poppins', 'Noto Sans JP', sans-serif",
  },
  shadows: {
    soft: "0 4px 12px rgba(47, 47, 47, 0.1)", // sumi ink wash depth
  },
  transitions: {
    smooth: "all 0.3s ease",
  },
};
