export const theme = {
  colors: {
    background: "#fdf6f9",      // sakura white-pink
    surface: "#fffafc",         // off-white panel
    accent: "#f7b8c6",          // cherry blossom pink
    accentLight: "#fbd3df",     // lighter pink
    text: "#333333",            // sumi ink black
    muted: "#5b5b5b",           // twilight gray
  },
  gradients: {
    button: "linear-gradient(135deg, #fbd3df 0%, #f7b8c6 100%)",
    hover: "linear-gradient(135deg, #ffeaf4 0%, #f5d0ea 100%)",
  },
  fonts: {
    main: "'Poppins', 'Noto Sans JP', sans-serif",
  },
  shadows: {
    soft: "0 4px 12px rgba(247, 184, 198, 0.2)",
  },
  transitions: {
    smooth: "all 0.3s ease",
  },
};
