import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { Card } from "../components/ui/Card";
import { Button } from "../components/ui/Button";

export default function QuizList() {
  const [quizzes, setQuizzes] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/quizzes").then((res) => setQuizzes(res.data));
  }, []);

  // Group quizzes by category
  const grouped = quizzes.reduce((acc, quiz) => {
    const cat = quiz.category || "Uncategorized";
    if (!acc[cat]) acc[cat] = [];
    acc[cat].push(quiz);
    return acc;
  }, {});

  return (
    <div
      style={{
        padding: "3rem",
        background: "linear-gradient(180deg, #fdf6f9 0%, #f9f0f3 100%)",
        minHeight: "100vh",
      }}
    >
      <h1
        style={{
          textAlign: "center",
          color: "#a5526c",
          marginBottom: "2rem",
          fontFamily: "'Poppins', 'Noto Sans JP', sans-serif",
        }}
      >
        My Quizzes
      </h1>

      {Object.keys(grouped).length === 0 ? (
        <p
          style={{
            textAlign: "center",
            color: "#777",
          }}
        >
          No quizzes yet. Create one!
        </p>
      ) : (
        Object.keys(grouped).map((category) => (
          <div key={category} style={{ marginBottom: "2.5rem" }}>
            <h2
              style={{
                color: "#d86b92",
                borderBottom: "2px solid #f7b8c6",
                paddingBottom: "0.3rem",
                marginBottom: "1rem",
              }}
            >
              {category}
            </h2>

            <div
              style={{
                display: "grid",
                gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
                gap: "1rem",
              }}
            >
              {grouped[category].map((q) => (
                <div
                  key={q.id}
                  style={{
                    background: "#fffafc",
                    borderRadius: "12px",
                    boxShadow: "0 4px 12px rgba(247, 184, 198, 0.2)",
                    padding: "1rem",
                    transition: "all 0.3s ease",
                  }}
                  onMouseEnter={(e) =>
                    (e.currentTarget.style.transform = "translateY(-4px)")
                  }
                  onMouseLeave={(e) =>
                    (e.currentTarget.style.transform = "translateY(0)")
                  }
                >
                  <h3 style={{ color: "#a5526c" }}>{q.title}</h3>
                  <p style={{ color: "#5b5b5b" }}>Type: {q.type}</p>

                  <div
                    style={{
                      marginTop: "0.8rem",
                      display: "flex",
                      gap: "0.5rem",
                    }}
                  >
                    <Link to={`/quiz/${q.id}`}>
                      <button
                        style={{
                          background: "#f7b8c6",
                          borderRadius: "8px",
                          border: "none",
                          padding: "0.5rem 1rem",
                          color: "white",
                          cursor: "pointer",
                          fontWeight: 600,
                        }}
                      >
                        Take
                      </button>
                    </Link>
                    <Link to={`/quiz/${q.id}/attempts`}>
                      <button
                        style={{
                          background: "#d86b92",
                          borderRadius: "8px",
                          border: "none",
                          padding: "0.5rem 1rem",
                          color: "white",
                          cursor: "pointer",
                          fontWeight: 600,
                        }}
                      >
                        Scores
                      </button>
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))
      )}
    </div>
  );
}
