import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

export default function QuizList() {
  const [quizzes, setQuizzes] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/quizzes").then((res) => setQuizzes(res.data));
  }, []);

  // Group quizzes by category
  const grouped = quizzes.reduce((acc, quiz) => {
    const cat = quiz.category || "Uncategorized";
    if (!acc[cat]) acc[cat] = [];
    acc[cat].push(quiz);
    return acc;
  }, {});

  return (
    <div style={{ padding: "2rem" }}>
      <h1>ðŸ“š My Quizzes</h1>

      {Object.keys(grouped).length === 0 ? (
        <p>No quizzes yet. Create one!</p>
      ) : (
        Object.keys(grouped).map((category) => (
          <div key={category} style={{ marginBottom: "2rem" }}>
            <h2 style={{ color: "#d86b92" }}>{category}</h2>
            {grouped[category].map((q) => (
              <div
                key={q.id}
                style={{
                  border: "1px solid #ddd",
                  borderRadius: "8px",
                  padding: "1rem",
                  marginBottom: "0.5rem",
                  background: "#fff7fa",
                }}
              >
                <h3>{q.title}</h3>
                <p>Type: {q.type}</p>

                <div style={{ marginTop: "0.5rem" }}>
                  <Link to={`/quiz/${q.id}`} style={{ marginRight: "1rem" }}>
                    ðŸŽ¯ Take Quiz
                  </Link>
                  <Link to={`/quiz/${q.id}/attempts`}>ðŸ“Š View Scores</Link>
                </div>
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
}
